void booksim2::Inject(BookSimEvent* event)
{
  int src = event->getSrc();
  int dest = event->getDest();

  // Size of packets injected to BookSim
  int size;
  if (_force_flitsize > 0)  size = _force_flitsize;
  else                      size = event->getSizeInFlits();

  // HANS: Delete this if not needed
  // assert(event->getSizeInFlits() == 1);

  // Sanity checks
  assert((src >= 0) && (src < _num_motif_nodes));
  assert((dest >= 0) && (dest < _num_motif_nodes));
  assert(size >= 0);

  // HANS: Again, very ugly hacks.. Fix this..
  int mapped_src, mapped_dest;

  mapped_src  = NodeMapping(src, _num_motif_nodes, _mapping_id);
  mapped_dest = NodeMapping(dest, _num_motif_nodes, _mapping_id);

  int pid = trafficManager->_InjectMotif(src, mapped_src, mapped_dest, size);
  assert(pid >= 0);

  _last_injection_time_vect[src] = GetSimTime();

  // Record the 'true' packet size
  // // _used_credits_vect[mapped_src].push(event->getSizeInFlits());

  // // HANS: For debugging purpose, delete if not needed
  // if (GetSimTime() > 10000)
    // printf("%d - BookSim inject with pid: %d from src: %d, mapped_src: %d, to dest: %d, mapped_dest: %d with size: %d at: %ld\n", GetSimTime(), pid, src, mapped_src, dest, mapped_dest,size, getCurrentSimCycle());

  booksim_event_bundle event_bundle;
  event_bundle.event = event;
  event_bundle.ejected = false;

  _injected_events[dest][src].push_back(make_pair(pid, event_bundle));

  // HANS: For debugging, don't use this for real simulation
  // std::cout << "Send back to: " << dest << std::endl;
  // _interface->send(dest, event);

}

void booksim2::WakeBookSim()
{
    _is_request_alarm = false;
    Cycle_t next_cycle = reregisterClock( _booksim_tc, _clock_handler );

    // HANS: FIXME, should read the clock_period from the configuration file
    int cycle_period = 1000;
    trafficManager->jumpTime((int)(getCurrentSimCycle() / cycle_period));
}

bool booksim2::IsRequestAlarm()
{
  return _is_request_alarm;
}


