void booksim2::PrintInjectionTime()
{
  int resolution = 100;

  int min = GetSimTime();
  int max = 0;

  for (int iter_node = 0; iter_node < _num_motif_nodes; iter_node++){
    if (_last_injection_time_vect[iter_node] < min){
      min = _last_injection_time_vect[iter_node];
    }

    if (_last_injection_time_vect[iter_node] > max){
      max = _last_injection_time_vect[iter_node];
    }
  }

  int min_cell = min / resolution;
  int max_cell = max / resolution;
  int num_cell = max_cell - min_cell + 1;

  assert(num_cell > 0);

  int count[num_cell] = {0};

  // Increment respective cell
  for (int iter_node = 0; iter_node < _num_motif_nodes; iter_node++){
    int cell_idx = (_last_injection_time_vect[iter_node] / resolution) - min_cell;
    count[cell_idx]++;
  }

  cout << "*** INJECTION TIME DISTRIBUTION *** " << endl;
  for (int iter_cell = 0; iter_cell < num_cell; iter_cell++){
    cout << (min_cell + iter_cell) * resolution << "\t" << count[iter_cell] << endl;
  }
  cout << "*** END ***" << endl;
}

